mapper.insert:
INSERT INTO IDEA
    (
        NAME
      , DESCRIPTION
    )
VALUE
    (
        #{name}
      , #{description}
    )

INSERT INTO IDEA_FUNC
    (
        IDEA_KID
      , NAME
      , DESCRIPTION
      , CHAR_KID
      , STAT
    )
VALUES

    (
        LAST_INSERT_ID()
      , #{fn.name}
      , #{fn.description}
      , #{fn.charger}
      , #{fn.stat}
    )

mapper.update:
UPDATE
    IDEA
SET
      NAME = #{name}
    , DESCRIPTION = #{description}
    , STAT = #{stat}
WHERE
    KID = #{kid}

UPDATE
    IDEA_FUNC
SET
          NAME = #{name}
        , DESCRIPTION = #{description}
        , CHAR_KID = #{charger}
        , STAT = #{stat}
WHERE
    KID = #{kid}

UPDATE
    PRIORITY
SET
    PRIORITY = #{priority}
WHERE
    DIVISION = #{division}
AND
    TYPE = #{type}

